    def check(self):
        f=open("questiontemporary.txt","r")
        list_str1=f.readlines()
        print(list_str1)
        for i in list_str1[0:20]:
            print(i.replace('\n',' '))
            self.textEdit_2.append(i.replace('\n',''))
        f.close()
    def Clear(self):
        f = open("questiontemporary.txt", "r+")
        f.truncate()
        f.close()
    def Clear1(self):
        h = open("answer.txt", "r+")
        h.truncate()
        h.close()
    def getanswer(self):
        count=100
        listanswer=[]
        listanswer.append(self.lineEdit.text())
        listanswer.append(self.lineEdit_2.text())
        listanswer.append(self.lineEdit_3.text())
        listanswer.append(self.lineEdit_4.text())
        listanswer.append(self.lineEdit_5.text())
        listanswer.append(self.lineEdit_6.text())
        listanswer.append(self.lineEdit_7.text())
        listanswer.append(self.lineEdit_8.text())
        listanswer.append(self.lineEdit_9.text())
        listanswer.append(self.lineEdit_10.text())
        f=open("answer.txt","r")
        listanswer_hold=f.readlines()
        listanswer_hold2=[i.replace('\n','') for i in listanswer_hold]
        print(listanswer_hold2,listanswer)
        for i in range(10):
            if listanswer[i]!=listanswer_hold2[i]:
                count=count-10
        print(listanswer_hold2[0])
        final_count=str(count)
        t=open("name.txt",'r+')
        list_name=t.readlines()
        print(list_name)
        name=''.join(list_name)
        if name=='':
            name='游客'
        self.textEdit.setText(name+"同学"+'您本次得分为： ' + final_count+'\n'+"您本次一共击败了"+str(assessment(count))+"%"+'的用户')
        if count<100:
            self.textEdit.append("本次测试的正确答案如下，希望对您有所帮助")
            for i in listanswer_hold2:
                self.textEdit.append(i)
        c=open("grades.txt","a")
        c.write(str(count)+'\n')
        t.close()
        g=open("name_grades.txt","a")
        i = datetime.datetime.now()
        g.write(name+'成绩为:'+' '+str(count)+'分'+'--'+str(i.year)+'年'+str(i.month)+'月'+str(i.day)+'日 '+str(i.hour)+'时'+str(i.minute)+'分'+'\n')
        u=open("user_grade_assess.txt","a")
        u.write(name+'于'+str(i.year)+'年'+str(i.month)+'月'+str(i.day)+'日 '+str(i.hour)+'时'+str(i.minute)+'分'+'得分为： '+final_count+'\n')
        u.write('一共击败了'+str(assessment(count))+"%"+'的用户 \n')
        u.write("作答情况如下： \n")
        num=1
        for i in listanswer:
            u.write(str(num)+': '+i+' ')
            num+=1
        u.write(' \n')
        u.close()
        g.close()
        f.close()
        h = open("answer.txt", "r+")
        h.truncate()
        h.close()
